    state: {...},
    getters: {...},
    mutations: {....},
    actions: {...}
}

const moduleB = {
    state: {...},
    getters: {...},
    mutations: {....},
    actions: {...}
}

const store = new Vuex.Store({
  modules: {
    a: moduleA,
    b: moduleB
  }
});

store.state.a // 获取moduleA的状态
store.state.b // 获取moduleB的状态

